import React from "react";
import axios from "../api/pokeapi";
import PokeImage from "./PokeImage";

class App extends React.Component {
  state = { pokemon: [] };
  pokemonRequest = async (pokeid) => {
    const response = await axios.get("/pokemon/35");

    console.log(response.data.name);
    this.setState({ pokemon: response.data.name });
  };

  render() {
    return (
      <div>
        <button onClick={this.pokemonRequest}>Click</button>
        <PokeImage pokemon={this.state.pokemon} />
      </div>
    );
  }
}

export default App;
